import { Component } from '@angular/core';
import {ConfirmationService, MessageService} from 'primeng/api';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  providers: [MessageService, ConfirmationService],
  templateUrl: './register.component.html'
})
export class RegisterComponent {
  constructor(private messageService: MessageService, private confirmationService: ConfirmationService, private router: Router) 
    {}

  register2() {
    console.log("hier")
    console.log('in register');
    const vornamef = document.getElementById('vorname') as HTMLInputElement;
    const nachnamef = document.getElementById('nachname') as HTMLInputElement;
    const emailf = document.getElementById('email') as HTMLInputElement;
    const passwordf = document.getElementById('password') as HTMLInputElement;
    //document.getElementById('password') as HTMLInputElement


    const vorname = vornamef.value;
    const nachname = nachnamef.value;
    const email = emailf.value;
    const password = passwordf.value;

    console.log(vorname, nachname, email, password);

    const url = 'https://ballermetrics-backend2.onrender.com/registration/register';

    console.log(url);

    var user = {
      firstName: vorname,
      lastName: nachname,
      email: email,
      password: password
    }

    console.log(user);

    var init: RequestInit = {
      method : 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(user)
    };

    console.log(init);



    fetch(url, init)
    .then(res => {
      if(!res.ok){
        console.log("fetch fehler");
      }
      return res.json();
    }).then(json => {
      localStorage.setItem('jwttoken', JSON.stringify({ token: json.token}));
      console.log(json);

      //var currentUser = JSON.parse(localStorage.getItem('currentUser'));
      //var token = currentUser.token; // your token
      console.log(localStorage.getItem('jwttoken'));
      this.router.navigate(['/login']);
    }).catch(err => {
      console.log(err);
      this.messageService.add({ severity: 'error', summary: 'Fehler beim Registieren. Cors kuss Jonas', life: 5000 });
    });


  }

}
<div class="login-body">
    <div class="login-wrapper">
        <div class="login-panel">
            <a [routerLink]="['/']" class="logo">
                <img src="assets/layout/images/logo.png" alt="poseidon-layout" />
            </a>
            <p-message></p-message>
            <div class="login-form">
                <h2>Registrieren</h2>
                <p>Hast du schon einen Account? <a [routerLink]="'/login'"> Login</a></p>
                <div class="p-fluid p-formgrid grid">
                    <div class="field col-12 md:col-6">
                        <span class="p-float-label">
                            <input type="text" id="vorname" pInputText >
                            <label for="inputtext">Vorname</label>
                        </span>
                    </div>
                    <div class="field col-12 md:col-6">
                        <span class="p-float-label">
                            <input type="text" id="nachname" pInputText >
                            <label for="inputtext">Nachname</label>
                        </span>
                    </div>
                    <div class="field col-12 md:col-12">
                        <span class="p-float-label">
                            <input type="text" id="email" pInputText >
                            <label for="inputtext">E-Mail</label>
                        </span>
                    </div>

                    <div class="field col-12 md:col-12">
                        <span class="p-float-label">
                            <input type="text" id="password" pInputText >
                            <label for="inputtext">Passwort</label>
                        </span>
                    </div>
                </div>

                <button style="background-color: #006eff;border-color: transparent;" pButton pRipple label="Registrieren" type="button" (click)="register2()" [routerLink]="'/login'"></button>

            </div>










            <p>BallerMetrics</p>


        </div>
    </div>
</div>
