import { Component } from '@angular/core';
import { BallerserviceService } from '../auth/ballerservice.service';

@Component({
  selector: 'app-login',
  templateUrl: './app.login.component.html',
})
export class AppLoginComponent {

  constructor(private authservice: BallerserviceService){};

  login()
  {
    const emailf = document.getElementById('email') as HTMLInputElement;
    const passwordf = document.getElementById('password') as HTMLInputElement;

    const email = emailf.value;
    const password = passwordf.value;

    console.log(email , password);

    this.authservice.login(email, password);
  }

}
<div class="login-body">
    <div class="login-wrapper">
        <div class="login-panel">
            <a [routerLink]="['/']" class="logo">
                <img src="assets/layout/images/logo.png" alt="poseidon-layout" />
            </a>
            <div class="login-form">
                <h2>Login</h2>
                <p>Hast du noch keinen Account? <a [routerLink]="'/register'"> Registrieren</a></p>
                <p>Wir haben Ihnen per Mail eine E-Mail geschickt.<br> Bitte betätigen Sie diese zuvor.</p>
                <input type="text" autocomplete="off" pInputText id="email" placeholder="Email">
                <input type="password" autocomplete="off" pInputText id="password" placeholder="Password">
                <button style="background-color: #006eff;border-color: transparent;" pButton pRipple label="LOGIN" type="button" (click)="login()"></button>

            </div>

            <p style="visibility: hidden;">A problem? <a href="#">Click here</a> and let us help you.</p>
        </div>
    </div>
</div>
