import { Component } from '@angular/core';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html'
})
export class RegisterComponent {

  register2() {
    console.log("hier")
    console.log('in register');
    const vornamef = document.getElementById('vorname') as HTMLInputElement;
    const nachnamef = document.getElementById('nachname') as HTMLInputElement;
    const emailf = document.getElementById('email') as HTMLInputElement;
    const passwordf = document.getElementById('password') as HTMLInputElement;
    //document.getElementById('password') as HTMLInputElement


    const vorname = vornamef.value;
    const nachname = nachnamef.value;
    const email = emailf.value;
    const password = passwordf.value;

    console.log(vorname, nachname, email, password);

    const url = 'https://ballermetrics-backend2.onrender.com/registration/register';

    console.log(url);

    var user = {
      firstName: vorname,
      lastName: nachname,
      email: email,
      password: password
    }

    console.log(user);

    var init: RequestInit = {
      method : 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(user)
    };

    console.log(init);



    fetch(url, init)
    .then(res => {
      if(!res.ok){
        console.log("fetch fehler");
      }
      return res.json();
    }).then(json => {
      localStorage.setItem('jwttoken', JSON.stringify({ token: json.token}));
      console.log(json);

      //var currentUser = JSON.parse(localStorage.getItem('currentUser'));
      //var token = currentUser.token; // your token
      console.log(localStorage.getItem('jwttoken'));

    }).catch(err => {
      console.log(err);
    });
  }

}
<div class="login-body">
    <div class="login-wrapper">
        <div class="login-panel">
            <a [routerLink]="['/']" class="logo">
                <img src="assets/layout/images/logo.png" alt="poseidon-layout" />
            </a>
            <div class="login-form">
                <h2>Registrierung</h2>
                <p>Du hast schon einen Account <a [routerLink]="'/login'"> Einloggen</a></p>
                <input type="text" autocomplete="off" pInputText placeholder="Vorname" id="vorname">
                <input type="text" autocomplete="off" pInputText placeholder="Nachname" id="nachname">
                <input type="text" autocomplete="off" pInputText placeholder="Email" id="email">
                <input type="password" autocomplete="off" pInputText placeholder="Passwort" id="password">
                <button pButton pRipple label="Registrieren" (click)="register2()" type="button"></button>
            </div>

            <p style="visibility: hidden;">A problem? <a href="#">Click here</a> and let us help you.</p>
        </div>
    </div>
</div>
